oducto':<?php echo $idProducto ?>};                $.ajax({                    type: "POST",                    url: "../envCam/crearComanda.php",                    data: dataString,                    success: function (data) {                        if (data === "OK") {                            $('.toast').text('Comanda añadida');                        } else {                            $('.toast').text('Error al añadir');                        }                        $('.toast').fadeIn(400).delay(2000).fadeOut(400);                        $("#pant").load(location.href + " #pant");                        $("#panellista").load(location.href + " #panellista");                        setTimeout(irA('listaTpv.php?idPedido=' + idPedido), 3000);                    }                });            }                        function crearPedido2(){                var crea = {'idMesa':mesa_global};                $.ajax({                    type: "GET",                    url: "../envCam/crearPedido.php",                    data: crea,                    success: function(idPedido2) {                        idPedido = idPedido2;                        crearComanda();                    }                });             }                        function cambio(idItem){                var item = $(idItem).val();                var env = {'idMesa':item};                mesa_global = item;                $.ajax({                    type: "POST",                    url: "compruebaMesa.php",                    data: env,                    success: function(data) {                        if (data === "OK"){                            document.getElementById('id010').style.display = 'none';                            crearPedido2();                        }                        if (data === "OC"){                            document.getElementById('id010').style.display = 'none';                            document.getElementById('id020').style.display = 'block';                        }                    }                });            }                        function abrirPedidos2() {                var mesa = {'idMesa':mesa_global};                $.ajax({                    type: "POST",                    url: "../envCam/consultaPedidos.php",                    data: mesa,                    success: function (data) {                        objeto = JSON.parse(data);                        document.getElementById('id030').style.display = 'block';                        w3DisplayData("idlista", objeto);                    }                });            }                        function añadir(pedido){                idPedido = pedido;                var pe = {'idPedido':idPedido};                $.ajax({                    type: "POST",                    url: "../envCam/compruebaAutopedido.php",                    data: pe,                    success: function (data) {                        if (data === "OK"){                            crearComanda();                        }else{                            idPedido = "";                            document.getElementById('id030').style.display = 'none';                            document.getElementById('id040').style.display = 'block';                        }                    }                });            }                        $(document).ready(function () {                $('#min').click(function () {                    //Solo si el valor del campo es mayor que 1                    if (parseInt($('#quantity').html()) > 1) {                        var val1 = parseInt($('#quantity').html()) - 1;                        var val2 = parseInt(val1);                        //Decrementamos su valor                        $('#quantity').html(val2);                    }                });                $('#plus').click(function () {                    var val1 = parseInt($('#quantity').html()) + 1;                    var val2 = val1.toString();                    //Aumentamos el valor del campo                    $('#quantity').html(val2);                });                $('#enviar').click(function () {                    if (idPedido===""){                        document.getElementById('id010').style.display = 'block';                    }else{                        crearComanda();                    }                });                               $('#nuevo').click(function() {                    crearPedido2();                });                                $('#textocomentario').click(function() {                    inicializaTeclado();                    document.getElementById('keyboard').style.display = 'block';                });                                $('#crear').click(function(){                    document.getElementById('keyboard').style.display = 'none';                    var idmesa = mesa_global;                    var mesa = {'idMesa':idmesa};                    $.ajax({                        type: "GET",                        url: "../envCam/recuperaPedido.php",                        data: mesa,                        success: function(idPedido3) {                            document.getElementById('id020').style.display = 'none';                            if (idPedido3 === "MULT"){                                abrirPedidos2();                            }else{                                idPedido = idPedido3;                                var pedido = {'idPedido':idPedido};                                $.ajax({                                    type: "POST",                                    url: "../envCam/compruebaAutopedido.php",                                    data: pedido,                                    success: function (data) {                                        if (data === "OK"){                                            crearComanda();                                        }else{                                            idPedido = "";                                            document.getElementById('id040').style.display = 'block';                                        }                                    }                                });                            }                          }                    });                });            });        </script>    </head>    <body class="w3-light-grey">            <div  style="height: 350px;">                <div class="w3-container w3-card-4 w3-theme w3-padding-8" id="cap">                    <div class="w3-left">                        <div class="w3-right w3-margin-left">                            <div id="divmesa" class="w3-xlarge"></div>                        </div>                    </div>                    <div class="w3-right">                        <div id="divpedido" class="w3-large" style="padding-top: 3px;"></div>                    </div>                </div>                <div style="height: 180px;text-align: center;padding-top: 10px" id="divimagen">                    <img src="../includes/imagen.php?id=<?php echo $row_RegProducto['id_imagen'] ?>&local=<?php echo $_SESSION['idEstablecimiento']; ?>" style="height: 170px;width: 190px;" class="w3-animate-zoom">                    <a class="w3-btn-floating-large w3-yellow w3-right w3-border-black" style="position:absolute;right:20px;" id="flot" onclick="document.getElementById('id050').style.display = 'block'">¡</a>                </div>                <div class="w3-center w3-margin-0" style="">                    <br>                    <h2 style="margin-top: 1px;margin-bottom: 1px" align="center"><?php echo recuperaNombreProducto($row_RegProducto['idProducto']) ?></h2>                        <div class="w3-large w3-center">Precio: <?php echo number_format($row_RegProducto['precioVentaProducto'], 2) ?>&euro;</div>       