                  document.getElementById('id001').style.display = 'none';                            crearPedido();                        }                        if (data === "OC"){                            document.getElementById('id001').style.display = 'none';                            document.getElementById('id002').style.display = 'block';                        }                        $("#progress").css("display","none");                    }                });            }                      function addComanda(){                $("#progress").css("display","block");                var mesa = {'idMesa':mesa_global};                $.ajax({                    type: "GET",                    url: "recuperaPedido.php",                    data: mesa,                    success: function(idPedido) {                        document.getElementById('id002').style.display = 'none';                        $("#progress").css("display","none");                        location.href = 'verCategoriasEnvCam.php?idPedido=' + idPedido;                    }                });             }                        function nuevoPedido(){                pedidoGlobal = "";                mesaGlobal = "";                irA('verCategoriasTpv.php');            }                      $(document).ready(function(){                                $('#liberar').click(function(){                    $("#progress").css("display","block");                    var lib = {'idMesa':mesa_global};                    $.ajax({                        type: "GET",                        url: "liberarMesa.php",                        data: lib,                        success: function() {                            document.getElementById('id002').style.display = 'none';                            $('.toast').fadeIn(400).delay(2000).fadeOut(400);                                                     document.getElementById('id001').style.display = 'block';                            $("#progress").css("display","none");                        }                    });                 });                                $('#crear').click(function(){                    addComanda();                });            });                    </script>    </head>    <body class="w3-light-grey">            <div style="height: 495px;">                <div style="height:30%;">                    <img src="../imagenes/EnvCamImages/imagen_mesa.png" style="width:100%;height:100%;">                </div>                <div class="w3-container w3-center" style="height: 70%;margin-top: 12px;padding-left: 4px;padding-right: 4px">                    <div class="w3-row w3-center" style="margin-bottom: 12px;height: 33%;">                        <div class="w3-col w3-theme w3-hover-light-blue" style="height: 100%; display: table;"                             onclick="nuevoPedido()">                            <div class="w3-large w3-center" style="display: table-cell;vertical-align: middle;">NUEVO PEDIDO</div>                        </div>                    </div>                                         <div class="w3-row" style="margin-bottom: 12px;height: 33%;">                        <div class="w3-col w3-theme w3-hover-light-blue" style="height: 100%; display: table;"                            onclick="irA('consultarPedidosTpv.php');">                           <div class="w3-large w3-center w3-padding-small" style="display: table-cell;vertical-align: middle;"><?php //echo $pedidos ?>PEDIDOS EN CURSO</div>                        </div>                    </div>                    <div class="w3-row" style="height: 33%;">                        <div class="w3-col w3-hover-light-blue" style="width:49%;float: left;height: 100%;display: table;background-color: #3f51b5;color: #fff;" id="btnpreparados"                             onclick="irA('consultarPreparadosTpv.php');">                          