 abrirSubMenus(idMenu, idSubMenu) {                var submenus = {'idMenu': idMenu, 'idSubmenu': idSubMenu};                $.ajax({                    type: "POST",                    url: "../envCam/consultaSubMenus.php",                    data: submenus,                    success: function (data) {                        objeto = JSON.parse(data);                        document.getElementById('id101').style.display = 'block';                        $('#textocomentario').val('');                        w3DisplayData("idIt", objeto);                    }                });            }                        function pedirMenu() {                if (completo) {                    if (idPedido !== "") {                        $("#progress").css("display","block");                        var idGrupoMenu = Math.floor(Math.random() * 10000);                        for (var a in objetoMenu.platosMenu) {                            var submenu = objetoMenu.platosMenu[a].idSubmenu;                            var product = objetoMenu.platosMenu[a].idProducto;                            var comentario = objetoMenu.platosMenu[a].coment;                            var linea = {'idPedido': idPedido, 'idMenu': idMenu, 'idSubmenu': submenu, 'idProducto': product, 'idGrupoMenu': idGrupoMenu, 'comentario': comentario};                            //alert (JSON.stringify(objetoMenu.platosMenu[a]));                            $.ajax({                                type: "POST",                                url: "../envCam/crearLineaMenu.php",                                data: linea,                                success: function (data) {                                    $("#progress").css("display","none");                                    $("#pant").load(location.href + " #pant");                                    irA('listaTpv.php?idPedido=' + idPedido);                                }                            });             