<?phprequire_once('../Connections/conexionComanderEnvCam.php');require_once('../includes/funcionesEnvCam.php');$precioMenus = 0;$idPedido = $_POST['idPedido'];mysqli_select_db($comcon, $database_comcon);if (isset($_POST['check'])){    $query_Total = "SELECT SUM(totalComanda) as total FROM comandas WHERE estadoCerradoComanda=0 AND cantidadEntregaComanda > 0 AND idGrupoMenuComanda IS NULL AND idPedidoComanda=" . $idPedido;}else{    $query_Total = "SELECT SUM(totalComanda) as total FROM comandas WHERE estadoCerradoComanda=0 AND idGrupoMenuComanda IS NULL AND idPedidoComanda=" . $idPedido;}$resultado = mysqli_query($comcon, $query_Total);$row = mysqli_fetch_assoc($resultado);$precio = $row["total"];$query_Menus = "SELECT * FROM comandas WHERE idPedidoComanda=" . $idPedido . " AND idGrupoMenuComanda IS NOT NULL GROUP BY idGrupoMenuComanda";$resultado_menus = mysqli_query($comcon, $query_Menus);$row_menus = mysqli_fetch_assoc($resultado_menus);$totalRows_menus = mysqli_num_rows($resultado_menus);if ($totalRows_menus > 0) {    do {        $precioMenus = $precioMenus + recuperaNombrePrecioMenu($row_menus['idMenuProductosComanda'], 2);    } while ($row_menus = mysqli_fetch_assoc($resultado_menus));}$precioTotal = $precio + $precioMenus;echo $precioTotal;?>