<?phprequire_once('../Connections/conexionComanderEnvCam.php');require_once('../includes/funcionesEnvCam.php');$precioMenus = 0;$tabla = $_POST['tabla'];mysqli_select_db($comcon, $database_comcon);$query_Total = "SELECT SUM(totalComandaTemp) as total FROM " . $tabla . " WHERE idGrupoMenuComandaTemp IS NULL";$resultado = mysqli_query($comcon, $query_Total);$row = mysqli_fetch_assoc($resultado);$precio = $row["total"];$query_Menus = "SELECT * FROM " . $tabla . " WHERE idGrupoMenuComandaTemp IS NOT NULL GROUP BY idGrupoMenuComandaTemp";$resultado_menus = mysqli_query($comcon, $query_Menus);$row_menus = mysqli_fetch_assoc($resultado_menus);$totalRows_menus = mysqli_num_rows($resultado_menus);if ($totalRows_menus > 0) {    do {        $precioMenus = $precioMenus + recuperaNombrePrecioMenu($row_menus['idMenuComandaTemp'], 2);    } while ($row_menus = mysqli_fetch_assoc($resultado_menus));}$precioTotal = $precio + $precioMenus;echo $precioTotal;?>