ript>        <script type="text/javascript">            var idPedido = getParameterByName('idPedido');            $(window).load(function () {                $("#progress").css("display","none");                $("#textonav").html("BUSCAR");                if (compruebaNotificaciones()){                    $("#notify").css("display","block");                    $("#notify").css("top","100px");                }                document.getElementById('myInput').focus();            });            function changeColor(x) {                x.style.background = "#C6D5FF";            }            function getParameterByName(name) {                name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");                var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),                        results = regex.exec(location.search);                return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));            }                        function myFunction() {                var input, filter, ul, li, a, i;                input = document.getElementById("myInput");                filter = input.value.toUpperCase();                ul = document.getElementById("myUL");                li = ul.getElementsByTagName("li");                for (i = 0; i < li.length; i++) {                    if (li[i].innerHTML.toUpperCase().indexOf(filter) > -1) {                        li[i].style.display = "";                    } else {                        li[i].style.display = "none";                    }                }            }                        function irA(prod){                $("#progress").css("display","block");                location.href = 'buscandoEnvCam.php?idProducto=' + prod + '&idPedido=' + idPedido;            }                    </script>    </head>    <body class="w3-light-grey">        <div class="w3-padding w3-display-middle" id="progress"><img src="page-loader.gif" width="100px" height="100px"></div>        <div class="userselect">            <?php include '../includes/menuNavegacionsinbuscar.html'; ?>            <br><br><br>            <input class="w3-input w3-border w3-padding" type="text" placeholder="Buscar producto.." id="myInput" onkeyup="myFunction()" style="position: fixed;">            <div onclick="w3_close()">                <?php if ($totalRows_RegProducto > 0) { ?>                <br><br>                    <ul class="w3-ul" id="myUL">                         <?php do { ?>                            <li onclick="irA(<?php echo $row_RegProducto['idProducto']; ?>);changeColor(this);" style="height:90px;padding-left:2px;">                                <img src="../includes/imagen.php?id=<?php echo $row_RegProducto['id_imagen']; ?>&local=<?php echo $_SESSION['idEstablecimiento']; ?>" class="w3-left w3-margin-right" style="width:80px;height:80px;">                                <div class="w3-large w3-margin-top"><?php echo utf8_encode($row_RegProducto['nombreProducto']); ?><br><?php echo number_format($row_RegProducto['precioVentaProducto'], 2); ?>&euro;</div>                            </li>                            <hr style="margin: 0px">                        <?php } while ($row_RegProducto = mysqli_fetch_assoc($RegProducto)); ?>                    </ul>                <?php } else { ?>                    <div>NO EXISTEN PRODUCTOS DE ESTA SUBCATEGORIA</div>                <?php } ?>            </div>            <footer class="w3-container w3-theme-l2 w3-bottom w3-padding-4">                Copyright 2016            </footer>        </div>    </body></html><?phpmysqli_free_result($RegProducto);?>