       if(!$encontrado){            $ArrayComandas[$a][0] = $row_RegComanda['cantidadEntregaComanda'];            $ArrayComandas[$a][1] = $row_RegComanda['idProductoComanda'];            $ArrayComandas[$a][2] = $row_RegComanda['totalComanda'];            $a++;        }        }    $query_RegMenu = "SELECT * FROM comandas WHERE estadoCerradoComanda=0 AND idGrupoMenuComanda IS NOT NULL AND idPedidoComanda=" . $idPedido . " GROUP BY idGrupoMenuComanda ORDER BY idMenuProductosComanda";    $RegMenu = mysqli_query($comcon, $query_RegMenu) or die(mysqli_connect_error());    $totalRows_RegMenu = mysqli_num_rows($RegMenu);    if ($totalRows_RegMenu > 0) {        $control = 1;    }    $a = 0;    while ($row_RegMenu = mysqli_fetch_assoc($RegMenu)){        $encontrado = false;        $long = count($ArrayMenus);        for($conta=0; $conta<= $long; $conta++) {            if ($ArrayMenus[$conta][1] == $row_RegMenu['idMenuProductosComanda']){                $ArrayMenus[$conta][0] = $ArrayMenus[$conta][0] + 1;                $ArrayMenus[$conta][1] = $row_RegMenu['idMenuProductosComanda'];                $ArrayMenus[$conta][2] = $ArrayMenus[$conta][2] + recuperaPrecioMenu($row_RegMenu['idMenuProductosComanda']);                $encontrado = true;            }        }        if(!$encontrado){            $ArrayMenus[$a][0] = 1;            $ArrayMenus[$a][1] = $row_RegMenu['idMenuProductosComanda'];            $ArrayMenus[$a][2] = recuperaPrecioMenu($row_RegMenu['idMenuProductosComanda']);            $a++;        }        }}?><!DOCTYPE html><html>    <head>        <meta charset="utf-8">        <title>Ver categorias</title>        <script type="text/javascript" src="jquery-mobile/jquery.js"></script>        <style type="text/css">            .userselect {                -webkit-user-select: none;  /* Chrome all / Safari all */                -moz-user-select: none;     /* Firefox all */                -ms-user-select: none;      /* IE 10