mesa").html('Mesa ' + nombreMesa);                $("#divpedido").html('Pedido: ' + idPedido);                $("#divfecha").html('Fecha: ' + fecha());                actualizaPrecio();            });                        function irAtras(){                location.href='listaTiempoRealEnvCam.php?idPedido='+idPedido;            }            function getParameterByName(name) {                name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");                var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),                        results = regex.exec(location.search);                return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));            }                        function fecha(){                var hoy = new Date();                var dd = hoy.getDate();                var mm = hoy.getMonth()+1; //hoy es 0!                var yyyy = hoy.getFullYear();                if(dd<10) {                    dd='0'+dd                }                 if(mm<10) {                    mm='0'+mm                }                 hoy = dd+'/'+mm+'/'+yyyy;                return hoy;            }            function actualizaPrecio() {                var actualiza = {'idPedido': idPedido, 'check': 1};                $.ajax({                    type: "POST",                    url: "actualizaPrecio.php",                    data: actualiza,                    success: function (data) {                        precioGlobal = data;                        var low = '';                        if (!isNaN(data)) {                            low = parseFloat(data).toFixed(2);                            if (isNaN(low)) {                                low = '0.00';                            }                            $("#divprecio").html(low + ' &euro;');                        } else {                            $('.toast').text('Error al actualizar');                            $('.toast').fadeIn(400).delay(2000).fadeOut(400);                        }                    }                });            }            $(document).ready(function () {                $('#btnticket').click(function () {                    $("#progress").css("display","block");                    var paga = {'idPedido': idPedido, 'precioTotal': precioGlobal};                    $.ajax({                        type: "POST",                        url: "cobrarPedido.php",                        data: paga,                        success: function (data) {                            var cadena = data.replace(/^\s+|\s+$/gm, '');                            if (cadena === 'OK') {                                $("#progress").css("display","none");                                $('.toast').text('Se imprimio el ticket y se validÃ³ el cobro correctamente');                                $('.toast').fadeIn(400).delay(2000).fadeOut(400);                                document.getElementById('id01').style.display = 'block';                            }else{                                $('.toast').text('Error al validar el pago');                                $('.toast').fadeIn(400).delay(2000).fadeOut(400);                            }                        }                    });                });            });        </script>    </head>    <body class="w3-light-grey">        <div class="w3-padding w3-display-middle" id="progress"><img src="page-loader.gif" width="100px" height="100px"></div>        <div class="userselect">            <header class="w3-container w3-card-4 w3-theme w3-padding-4 w3-top" id="cap">                <div class="w3-left">                    <div class="w3-left w3-margin-top">                        <i class="material-icons w3-xlarge" onclick="irAtras();">&#xE5C4;</i>                    </div>                    <div class="w3-right w3-margin-left">                        <div id="divmesa" class="w3-xlarge"></div>