ipt type="text/javascript">            $(window).load(function () {                var medida = "";                $("#progress").css("display","none");                $("#textonav").html("PREPARADOS");                medida = $( window ).height() - 55;                $("#notify").css("top",medida);                compruebaNotificaciones();            });                        function recarga(){                setInterval(function(){ $("#pant").load(location.href + " #pant"); },5000);            }                        function borrarNotificacion(pedido){                var borrar = {'idPedido': pedido};                $.ajax({                    type: "POST",                    async: true,                    url: "borrarNotificacion.php",                    data: borrar,                    success: function (data) {                    }                });               }                    </script>    </head>    <body class="w3-light-grey" onload="recarga();">        <div class="w3-padding w3-display-middle" id="progress"><img src="page-loader.gif" width="100px" height="100px"></div>        <div class="userselect">            <?php include '../includes/menuNavegacionsinbuscar.html'; ?>            <br><br><br>            <div id="pant">                <?php if ($totalRows_preparados > 0) { ?>                    <div class="w3-container w3-theme-l2 w3-center">                        <h4>COMANDAS PREPARADAS</h4>                    </div>                    <div style="border-bottom: #706e6e solid 1px;"></div>                    <ul class="w3-ul w3">                        <?php do { ?>                        <li class=" w3-hover-light-blue w3-margin-left w3-margin-right" style="border-bottom: #706e6e solid 1px;" onclick="borrarNotificacion(<?php echo $row_preparados['idPedidoComanda']; ?>);ir('listaTiempoRealEnvCam.php?idMesa=<?php echo $row_preparados['idMesaComanda']; ?>&idPedido=<?php echo $row_preparados['idPedidoComan