   var myObject = JSON.parse(this.responseText);                    w3DisplayData("idItem", myObject);                }            });            function getParameterByName(name) {                name = name.replace(/[\[]/, "\\[").replace(/[\]]/, "\\]");                var regex = new RegExp("[\\?&]" + name + "=([^&#]*)"),                results = regex.exec(location.search);                return results === null ? "" : decodeURIComponent(results[1].replace(/\+/g, " "));            }                        function crearComanda(){                $("#progress").css("display","block");                var quantity = $('#quantity').html();                var comentario = $('#textocomentario').val();                var dataString = {'idPedido': idPedido, 'cantidad': quantity, 'comentario' : comentario, 'idProducto':<?php echo $idProducto ?>};                $.ajax({                    type: "POST",                    url: "crearComanda.php",                    data: dataString,                    success: function (data) {                        if (data === "OK") {                            $('.toast').text('Comanda añadida');                        } else {                            $('.toast').text('Error al añadir');                        }                        $('.toast').fadeIn(400).delay(2000).fadeOut(400);                        setTimeout(location.href = 'listaTiempoRealEnvCam.php?idPedido=' + idPedido, 3000);                        $("#progress").css("display","none");                    }                });            }                        function crearPedido(){                $("#progress").css("display","block");                var crea = {'idMesa':mesa_global};                $.ajax({                    type: "GET",                    url: "crearPedido.php",                    data: crea,                    success: function(idPedido2) {                        document.getElementById('id02').style.display = 'none';                        $('.toast').text('Pedido creado');                        $('.toast').fadeIn(400).delay(2000).fadeOut(400);                        idPedido = idPedido2;                        $("#progress").css("display","none");                        crearComanda();                    }                });             }                       function cambio(idItem){                var item = $(idItem).val();                var env = {'idMesa':item};                mesa_global = item;                $.ajax({                    type: "POST",                    url: "compruebaMesa.php",                    data: env,                    success: function(data) {                        if (data === "OK"){                            document.getElementById('id01').style.display = 'none';                            crearPedido();                        }                        if (data === "OC"){                            document.getElementById('id01').style.display = 'none';                            document.getElementById('id02').style.display = 'block';                        }                    }                });            }                        function abrirPedidos() {                console.log('mesa ' + mesa_global);                var mesa = {'idMesa':mesa_global};                $.ajax({                    type: "POST",                    url: "consultaPedidos.php",                    data: mesa,                    success: function (data) {                        objeto = JSON.parse(data);                        document.getElementById('id010').style.display = 'block';                        w3DisplayData("idlista", objeto);                    }                });            }                        function añadir(pedido){                idPedido = pedido;                var pe = {'idPedido':idPedido};                $.ajax({                    type: "POST",                    url: "compruebaAutopedido.php",                    data: pe,                    success: function (data) {                        if (data === "OK"){                            crearComanda();                        }else{                            idPedido = "";                            document.getElementById('id010').style.display = 'none';                            document.getElementById('id011').style.display = 'block';                        }                    }                });            }            $(document).ready(function () {                $('#min').click(function () {                    //Solo si el valor del campo es mayor que 1                    if (parseInt($('#quantity').html()) > 1) {                        var val1 = parseInt($('#quantity').html()) - 1;                        var val2 = parseInt(val1);                        //Decrementamos su valor                        $('#quantity').html(val2);                    }                });                $('#plus').click(function () {                    var val1 = parseInt($('#quantity').html()) + 1;                    var val2 = val1.toString();                    //Aumentamos el valor del campo                    $('#quantity').html(val2);                });                $('#enviar').click(function () {                    if (idPedido===""){                        document.getElementById('id01').style.display = 'block';                    }else{                        crearComanda();                    }                });                               $('#nuevo').click(function() {                    crearPedido();                });                                $('#crear').click(function(){                    var idmesa = mesa_global;                    var mesa = {'idMesa':idmesa};                    $.ajax({                        type: "GET",                        url: "recuperaPedido.php",                        data: mesa,                        success: function(idPedido3) {                            document.getElementById('id02').style.display = 'none';                            if (idPedido3 === "MULT"){                                abrirPedidos();                            }else{                                idPedido = idPedido3;                                var pedido = {'idPedido':idPedido};                                $.ajax({                                    type: "POST",                                    url: "compruebaAutopedido.php",                                    data: pedido,                                    success: function (data) {                                        if (data === "OK"){                                            crearComanda();                                        }else{                                            idPedido = "";                                            document.getElementById('id011').style.display = 'block';                                        }                                    }                                });                            }                          }                    });                });            });        </script>    </head>    <body class="w3-light-grey">        <div class="w3-padding w3-display-middle" id="progress"><img src="page-loader.gif" width="100px" height="100px"></div>        <div class="userselect">            <?php include '../includes/menuNavegacionsinbuscar.html'; ?>            <br><br><br>            <div  class="pantalla">                <div style="height: 50%;" id="divimagen">                    <img src="../includes/imagen.php?id=<?php echo $row_RegProducto['id_imagen'] ?>&local=<?php echo $_SESSION['idEstablecimiento']; ?>" style="height: 100%;width: 100%;" class="w3-animate-zoom">                    <a class="w3-btn-floating-large w3-yellow w3-right w3-border-black" style="position:absolute;right:16px;" id="flot" onclick="d