           <button class="w3-btn w3-indigo w3-small" style="width:45%;margin-right: 5px;" id="crear">AÑADIR A PEDIDO</button>                        <button class="w3-btn w3-indigo w3-small" style="width:45%;" id="nuevo">NUEVO PEDIDO</button>                    </div>                </div>            </div>                        <div id="id010" class="w3-modal">                <div class="w3-modal-content w3-card-8 w3-round-medium" style="width:70%;">                    <header class="w3-container w3-theme-l2">                        <span onclick="document.getElementById('id010').style.display = 'none'"                              class="w3-closebtn">&times;</span>                        <h4>SELECCIONAR PEDIDO</h4>                    </header>                    <div class="w3-container w3-large w3-margin" style="height: 240px;overflow: scroll;">                        <ul id="idlista" class="w3-ul w3-card-4">                            <li class="w3-hover-light-blue" w3-repeat="clientes" id="{{idPedido}}" value="{{idPedido}}" onclick="añadir({{idPedido}});">PEDIDO Nº {{idPedido}}</li>                        </ul>                    </div>                    <footer class="w3-container w3-theme-l2" style="height: 3px;"></footer>                </div>            </div>                        <div id="id011" class="w3-modal">                <div class="w3-container w3-section w3-center w3-yellow">                <span onclick="document.getElementById('id011').style.display = 'none'"                               class="w3-closebtn">&times;</span>                    <h3>¡ATENCION!</h3>                    NO SE PUEDE AÑADIR A UN PEDIDO YA COBRADO                    <div class="w3-margin-top w3-margin-bottom w3-center">                        <button class="w3-btn w3-indigo w3-small" style="width:45%;" onclick="document.getElementById('id011').style.display = 'none'">CERRAR</button>                    </div>                </div>       