ntalla').width() < 350){                    $("#tituloheader").html('INICIO');                    $("#divnotif").html('MENSAJES');                    $("#iconopersona").css("margin-left",0);                }                //compruebaNotificaciones();                compruebaPreparados();                w3Http("prueba_mesas.php", function () {                    if (this.readyState == 4 && this.status == 200) {                        var myObject = JSON.parse(this.responseText);                        w3DisplayData("idItem", myObject);                    }                });            });                      function compruebaPreparados(){                $.ajax({                    type: "POST",	                            url: "compruebaPreparados.php",                    async: true,                    success: function (data) {                        cadena = data.replace(/^\s+|\s+$/gm, '');                        if (cadena !== '0') {                            $("#btnpreparados").css("background-color","#ffeb3b");                            $("#btnpreparados").css("color","#000");                            $("#badge").css("display","block");                            $("#badge").html('(' + cadena + ')');                        }else{                            $("#btnpreparados").css("background-color","#3f51b5");                            $("#btnpreparados").css("color","#fff");                            $("#badge").css("display","none");                            $("#badge").html('(' + cadena + ')');                        }                    }                });                                $.ajax({                    type: "POST",	                            url: "compruebaNotificaciones.php",                    async: true,                    success: function (data) {                        cadena = data.replace(/^\s+|\s+$/gm, '');                        if (cadena === 'SI') {                            $("#btnnotificaciones").css("background-color","#ffeb3b");                            $("#btnnotificaciones").css("color","#000");                        }else{                            $("#btnnotificaciones").css("background-color","#3f51b5");                            $("#btnnotificaciones").css("color","#fff");                        }                    }                });                setTimeout('compruebaPreparados()', 12000);            }                      function cambio(idItem){                $("#progress").css("display","block");                var item = $(idItem).val();                var env = {'idMesa':item};                mesa_global = item;                $.ajax({                    type: "POST",                    url: "compruebaMesa.php",                    data: env,                    success: function(data) {                        if (data === "OK"){                            document.getElementById('id01').style.display = 'none';                            crearPedido();                        }                        if (data === "OC"){                            document.getElementById('id01').style.display = 'none';                            document.getElementById('id02').style.display = 'block';                        }                        $("#progress").css("display","none");                    }                });            }                       function crearPedido(){                $("#progress").css("display","block");                var crea = {'idMesa':mesa_global};                $.ajax({                    type: "GET",                    url: "crearPedido.php",                    data: crea,                    success: function(idPedido) {                        document.getElementById('id02').style.display = 'none';                        $('.toast').text('Pedido creado');                        $('.toast').fadeIn(400).delay(2000).fadeOut(400);                        $("#progress").css("display","none");                        location.href = 'verCategoriasEnvCam.php?idPedido=' + idPedido;                    }                });             }                      function addComanda(){                $("#progress").css("display","block");                var mesa = {'idMesa':mesa_global};                $.ajax({                    type: "GET",                    url: "recuperaPedido.php",                    data: mesa,                    success: function(idPedido) {                        document.getElementById('id02').style.display = 'none';                        $("#progress").css("display","none");                        location.href = 'verCategoriasEnvCam.php?idPedido=' + idPedido;                    }                });             }                      $(document).ready(function(){                                $('#liberar').click(function(){                    $("#progress").css("display","block");                    var lib = {'idMesa':mesa_global};                    $.ajax({                        type: "GET",                        url: "liberarMesa.php",                        data: lib,                        success: function() {                            document.getElementById('id02').style.display = 'none';                            $('.toast').fadeIn(400).delay(2000).fadeOut(400);                                                     document.getElementById('id01').style.display = 'block';                            $("#progress").css("display","none");                        }                    });                 });                                $('#crear').click(function(){                    addComanda();                });            });                    </script>    </head>    <body class="w3-light-grey">        <div class="loader"></div>        <div class="w3-padding w3-display-middle" id="progress"><img src="page-loader.gif" width="100px" hei