nu'];	}	if ($cual == 2){		$devuelve = $row_RegSubMenu['precioMenu'];	}	mysqli_free_result($RegSubMenu);	return $devuelve;}function recuperaTipoMenu($idMenu){	global $database_comcon, $comcon;	mysqli_select_db($comcon, $database_comcon);	$query_RegTipoMenu = "SELECT menus.idTipoMenu FROM menus WHERE menus.idMenu=$idMenu";	$RegTipoMenu = mysqli_query($comcon, $query_RegTipoMenu) or die(mysqli_connect_error());	$row_RegTipoMenu = mysqli_fetch_assoc($RegTipoMenu);	$totalRows_RegTipoMenu = mysqli_num_rows($RegTipoMenu);	mysqli_free_result($RegTipoMenu);        $letra = "M";	if ($row_RegTipoMenu['idTipoMenu'] == 2){		$letra = "O";	}	return $letra;}function recuperaClave($idUser){	global $database_comcon, $comcon;	mysqli_select_db($comcon, $database_comcon);	$query_RegClave = "SELECT usuarios.claveUsuario FROM usuarios WHERE usuarios.idUsuario=$idUser";	$RegClave = mysqli_query($comcon, $query_RegClave) or die(mysqli_connect_error());	$row_RegClave = mysqli_fetch_assoc($RegClave);	$totalRows_RegClave = mysqli_num_rows($RegClave);	mysqli_free_result($RegClave);	return $row_RegClave['claveUsuario'];}function todoCompleto($mesa){    $resultado = false;    global $database_comcon, $comcon;    mysqli_select_db($comcon, $database_comcon);    $query_Cuenta = "SELECT idPedido FROM pedidos WHERE idMesaPedido=$mesa AND estadoMesaPedido=1";    $Cuenta = mysqli_query($comcon, $query_Cuenta) or die(mysqli_error());    $row_Cuenta = mysqli_fetch_assoc($Cuenta);    $totalRows_Cuenta = mysqli_num_rows($Cuenta);        $query_Completo = "SELECT idPedido FROM pedidos WHERE idMesaPedido=$mesa AND estadoMesaPedido=1 AND estadoCobroPedido=1 AND estadoEntregadoPedido=1";    $Completo = mysqli_query($comcon, $query_Completo) or die(mysqli_connect_error());    $row_Completo = mysqli_fetch_assoc($Completo);    $totalRows_Completo = mysqli_num_rows($Completo);        if ($totalRows_Completo == $totalRows_Cuenta){       $resultado = true;     }    return $resultado;}?>