width;            var local = [[],[]];            var map ;            var image;            var contentString;            var myIndex = 0;            var maxHeight;                        $(window).load(function () {                $('.pantalla').height($( window ).height()-60);            });            // Script to open and close sidenav            function w3_open() {                document.getElementById("mySidenav").style.display = "block";                document.getElementById("myOverlay").style.display = "block";            }            function w3_close() {                document.getElementById("mySidenav").style.display = "none";                document.getElementById("myOverlay").style.display = "none";            }                       //FUNCION PARA AUTOCOMPLETAR BUSQUEDA DE DIRECCIONES CON GOOGLE            function initAutocomplete() {                console.log("initAutocomplete()");                var input = document.getElementById('autocomplete');                var autocomplete = new google.maps.places.Autocomplete(input);                google.maps.event.addListener(autocomplete, 'place_changed', function () {                    var place = autocomplete.getPlace();                    lat = place.geometry.location.lat();                    lng = place.geometry.location.lng();                    latitudActual = lat;                    longitudActual = lng;                    initMap(14);                });                geolocation();            }                        //FUNCION PARA HACER CLICK EN EL INPUT            function clickInput() {                console.log("clickInput()");                $("#autocomplete").click();                $("#autocomplete").val('');            }                        //FUNCION PARA RECUPERAR DIRECCION A PARTIR DE COORDENADAS            function recoveryDirection() {                console.log("recoveryDirection()");                var geocoder = new google.maps.Geocoder();                var yourLocation = new google.maps.LatLng(latitudActual, longitudActual);                geocoder.geocode(                        {'latLng': yourLocation},                        processGeocoder                                                      );            }                        //FUNCION PARA DESCARGAR LA LOCALIZACION DE LOS LOCALES            function filtrar(lat, lng, distance) {                console.log("filtrar()");                var imagen;                var nombreEstablecimiento;                var latitud;                var longitud;                var cantidad = 0;                var puntuacion;                var cant_comentarios;                var direction;                var apertura;                var cierre;                var distancia;                var idEstablecimiento;                var definicion;                var data = {'lat': lat, 'lng': lng, 'distance': distance, 'idioma': idioma};                var ajax = $.ajax({                    type: "POST",                    data: data,                    dataType: "jsonp",                    url: 'http://comander.es/Comander/Usuario/filtrarEstablecimientos.php',                    crossDomain: true,                    cache: false,                    timeout: 5000,                    asinc: true                });                ajax.done(function (data) {                                        if (data == 'vacio') {                                            } else {                        $.each(data, function (id, establecimiento) {                            $.each(establecimiento, function () {                                imagen = establecimiento['imagen'];                                nombreEstablecimiento = establecimiento['nombre_establecimiento'];                                latitud = establecimiento['lat'];                                longitud = establecimiento['lng'];                                puntuacion = parseInt(establecimiento['puntuacion']);                                cant_comentarios = establecimiento['cant_comentarios'];                                direction = establecimiento['direction'];                                apertura = establecimiento['apertura'];                                cierre = establecimiento['cierre'];                                distancia = parseFloat(establecimiento['distance']);                                idEstablecimiento = establecimiento['id'];                                definicion = establecimiento['definicion'];                            });                            cantidad++;                            markers(latitud, longitud, nombreEstablecimiento, idEstablecimiento, direction,                             puntuacion, cant_comentarios, definicion, apertura, cierre);                             addImagesRestaurantes(idEstablecimiento);                        });                    }                    carousel();                });                ajax.fail(function (jqXHR, textStatus, errorThrown) {                    loading('ocultar');                    if (textStatus === "timeout") {                        document.getElementById('sinConexion').style.display = 'block';                    } else {                        toast("Ha sucedido un error intentalo de nuevo más tarde");                    }                });            }                        function imprimir(){                console.log("imprimir()");                clickInput();                            }            //FUNCION PARA PASAR COODENADAS A TEXTO EN INPUT DEL MAPA            function processGeocoder(results, status) {                console.log("processGeocoder()");                if (status == google.maps.GeocoderStatus.OK) {                    if (results[0]) {                        $("#autocomplete").val(results[0].formatted_address);                        initMap(12);                    } else {                        error('Google no retorno resultado alguno.');                    }                } else {                    error("Geocoding fallo debido a : " + status);                }            }                             //FUNCION GEOLOCALIZACION              function geolocation() {                console.log("geolocation()");                jQuery.ajax( {                    url: '//freegeoip.net/json/',                    type: 'POST',                    dataType: 'jsonp',                    success: function(location) {                      console.log(location.city);                      console.log(location.longitude);                      console.log(location.latitude);                      latitudActual = location.latitude;                      longitudActual = location.longitude;                      recoveryDirection();                    }                });            }                                 //FUNCION PARA EL HORARIO DE APERTURA            function horario(apertura, cierre){                var horario;                if(apertura == null || apertura == '00:00' && cierre == '00:00'){                    horario = 'Hoy cerrado';                } else {                    horario = 'Hoy de: ' + apertura + ' hasta: ' + cierre;                }                return horario;            }                        //FUNCION PARA CREAR VENTANA DE INFORMACION EN MAPA            function openWindow(nombreEstablecimiento, direction, idEstablecimiento, puntuacion, cant_comentarios, definicion, apertura, cierre){                console.log("openWindow()");                                  var horarios = horario(apertura, cierre);                var puntuacionString = '<div id="puntuacion" class="ec-stars-wrapper" style="margin-left:65px;">';                                var puntuacion = puntuacion;                var restante = 5 - puntuacion;                var estrellasAmarillas = '<a style="color:#d2be0e">&#9733;</a>';                var estrellasNegras = '<a style="color:#bdbdc0">&#9733;</a>';                for (var i = 0; i < puntuacion; i++) {                    puntuacionString += estrellasAmarillas;                }                for (var a = 0; a < restante; a++) {                    puntuacionString += estrellasNegras;                }                                 contentString =                         '<div id="content" style="width:250px;height:250px;">' +                             '<div id="cabecera"  align=center><b>' + nombreEstablecimiento + '</b></div>' +                             '<br>' +                            '<div id="imagen">' +                                 '<img src="../includes/imagenEstablecimientos.php?id=' + idEstablecimiento + '" width="250px;" height="75px;" />' +                            '</div>' +                             '<div id="cuerpo" style="padding-top:5px;" align="center">' + direction + '</div>' +                               puntuacionString + '</div>' +                            '<div id="comentarios" align="center">' +                                 '<a class="w3-medium">(' + cant_comentarios + ')' + ' Comentarios</a>' +                             '</div>' +                             '<div id="definicion" style="margin-top:10px;" align="center">' + definicion + '</div>' +                             '<div id="horario" style="margin-top:10px;" align="center"><b>' + horarios + '</b></div>' +                         '</div>';            }                                    //FUNCION PARA CREAR ICONOS             function imagen(){                console.log("imagen()");                    image = {                url: 'img/manoIcon2.png',                  // This marker is 20 pixels wide by 32 pixels high.                size: new google.maps.Size(32, 48),                // The origin for this image is (0, 0).                origin: new google.maps.Point(0, 0),                // The anchor for this image is the base of the flagpole at (0, 32).                anchor: new google.maps.Point(0, 32)                };                filtrar(40.4167754, -3.7037901999999576, 10000);            }                        function markers(lat, long, nombreEstablecimiento, idEstablecimiento, direction, puntuacion, cant_comentarios, definicion, apertura, cierre){                                console.log("markers()");                 openWindow(nombreEstablecimiento, direction, idEstablecimiento, puntuacion, cant_comentarios, definicion, apertura, cierre);                var myLatLng = new google.maps.LatLng(lat, long);                var marker = new google.maps.Marker({                    position: myLatLng,                    icon: image                });                var infowindow = new google.maps.InfoWindow({                      content: contentString,                      disableAutoPan: true                    });                marker.setMap(map);                marker.addListener('click', function(){                    window.location.href = 'verInfoLocalPlataforma.php?id=' + idEstablecimiento;                });                marker.addListener('mouseover', function(){                    infowindow.open(map, marker);                });               marker.addListener('mouseout', function(){                    infowindow.close(map, marker);                });                            }            //FUNCION PARA INICIAR MAPA            function initMap(zoom) {                console.log("initMap()");                var zom = 5;                if (zoom != undefined){                    zom = zoom;                }                var myLatLng = new google.maps.LatLng(latitudActual, longitudActual);                var mapOptions = {                    zoom: zom,                    mapTypeId: google.maps.MapTypeId.ROADMAP,                    center: myLatLng                }                map = new google.maps.Map(document.getElementById("googleMap"), mapOptions);                imagen();            }                        //FUNCION PARA IR PASANDO IMAGENES            function carousel() {                console.log("carousel()");                   var i;                var x = document.getElementsByClassName("mySlides");                for (i = 0; i < x.length; i++) {                    x[i].style.display = "none";                }                myIndex++;                if (myIndex > x.length) {                    myIndex = 1;                }                x[myIndex - 1].style.display = "block";                setTimeout(carousel, 10000);            }                        //FUNCION PARA AÑADIR IMAGENES AL BOTON D VER RESTAURANTES            function addImagesRestaurantes(imagenestablecimiento) {                console.log("addImagesRestaurantes()");                  var registro = '<img class="mySlides w3-border" src="../includes/imagenEstablecimientos.php?id=' + imagenestablecimiento + '" class="w3-round-small" alt="Norway" style="height:300px;width:90%;margin-left: 30%;margin-top:0px;padding: 10px;">';                $("#fotorest").append(registro);            }                                  $(document).ready(function () {                 height = $(window).height();                width = $(window).width();                var content = "max-width:900px;margin-top:100px;height:" + (height-100) + "px;";                $("#content2").attr("style", content);                clickInput();                $("#").focus().click();                $("#autocomplete").click(function(){                    $("#autocomplete").val('');                   });              });</script><?php $editFormAction = $_SERVER['PHP_SELF'];if (isset($_SERVER['QUERY_STRING'])) {    $editFormAction .= "?" . htmlentities($_SERVER['QUERY_STRING']);}if ((isset($_POST["MM_update"])) && ($_POST["MM_update"] == "form1")) {    $destinatario = 'comandersoftware@gmail.com' ;     $name = filter_input(INPUT_POST, 'name');     $email = filter_input(INPUT_POST, 'email');    $asunto = "Correo de cliente/Usuario.";    $mensaje = filter_input(INPUT_POST, 'message');     $cuerpo = "Nombre: ".$name."\n".                "Email: ".$email."\n\n".                "Mensaje: ".$mensaje;     mail($destinatario,$asunto,$cuerpo) ;       $resupeatAuto = "Respuesta Automática. No responda a esta dirección";    $mensageRespuestaAuto = "Hola ".$name." hemos recibido tu correo electrónico.\n"            . "En breve nos pondremos en contacto contigo.\n"            . "Gracias y un saludo.\n\n"            . "Comander Software";    mail($email,$resupeatAuto,$mensageRespuestaAuto);}?><body>    <!-- Links (sit on top) --><div style="position: fixed; top: 160px;left: 65px;">    <div>        <a href="https://www.facebook.com/Comander-267644900328502/"><img src="img/fb.png" alt="" width="40" height="40" style="margin-bottom: 16px;"/></a>    </div>    <div>        <a href="https://twitter.com/Comander_es"><img src="img/twitter.png" alt="" width="40" height="40"/></a>    </div>    <div>        <a href="https://plus.google.com/114923420313270746620"><img src="img/google.png" alt="" width="40" height="40" style="margin-top: 16px;"/></a>    </div></div>   <!-- Navbar (sit on top) --><div class="w3-top">  <div